import {useMemo} from "react";

export const usePagination = (totalPages, limit) => {
    const pagesArray = useMemo(() => {
        let pages =[];

        for (let i = 0; i < totalPages; i++) {
            pages.push(i+1);
        }
        return pages;
    }, [totalPages,limit]);

    return pagesArray;
}
